name.value:
{Pt(
                        let $addSome := function($i as xs:int, $f as function(xs:int, function(*)) as xs:int) as xs:int
                        {
                          if ($i > 3) then $i
                          else $f($i + 1, $f)
                        }
                        return apply($addSome, [1, $addSome]))}
